import * as express from 'express';
import * as compression from 'compression';  // compresses requests
import * as session from 'express-session';
import * as bodyParser from 'body-parser';
import * as logger from 'morgan';
import * as lusca from 'lusca';
import * as dotenv from 'dotenv';
import * as mongo from 'connect-mongo';
import * as mongoose from 'mongoose';
import * as expressValidator from 'express-validator';
import * as bluebird from 'bluebird';
import * as expressJwt from 'express-jwt';
import * as swaggerUI from 'swagger-ui-express';
import * as swaggerDocument from '../swagger.json';
import { AuthRouter, SwaggerAPIRouter, UserRouter } from './routes';

const MongoStore = mongo(session);

// Load environment variables from .env file, where API keys and passwords are configured
dotenv.config({path: '.env' || '.env.example'});

// Create Express server
const app = express();

// Connect to MongoDB
const mongoUrl = process.env.MONGODB_URI;
(<any>mongoose).Promise = bluebird;
mongoose.connect(mongoUrl, {useMongoClient: true}).then(
  () => { /** ready to use. The `mongoose.connect()` promise resolves to undefined. */
  },
).catch(err => {
  console.log('MongoDB connection error. Please make sure MongoDB is running. ' + err);
  // process.exit();
});

// Express configuration
app.set('port', process.env.PORT || 3000);
app.use(compression());
app.use(logger('dev'));
app.use(bodyParser.json());
app.use(bodyParser.urlencoded({extended: true}));
app.use(expressValidator());
app.use(session({
  resave: true,
  saveUninitialized: true,
  secret: process.env.SESSION_SECRET,
  store: new MongoStore({
    url: mongoUrl,
    autoReconnect: true
  })
}));
app.use(lusca.xframe('SAMEORIGIN'));
app.use(lusca.xssProtection(true));

app.use(expressJwt({
    secret: process.env.JWT_SECRET,
    requestProperty: 'auth',
    getToken: function fromHeader(req: express.Request) {
      const tokenHeader = req.headers.Authorization || req.headers.authorization;
      if (tokenHeader && (tokenHeader as string).split(' ')[0] === 'Bearer') {
        return (tokenHeader as string).split(' ')[1];
      }
    }
  })
    .unless({path: [/\/api-docs\//g, {url: '/', method: 'OPTIONS'}, /\/auth\//g]})
);

app.use(function (err, req, res, next) {
  if (err.name === 'UnauthorizedError') {
    res.status(401).send({
      msg: 'Invalid or no token supplied',
      code: 401
    });
  }
});

app.use('/auth', AuthRouter);
app.use('/users', UserRouter);
/**
 * Add swagger endpoints
 */
app.use('/api-docs', swaggerUI.serve, swaggerUI.setup(swaggerDocument));
app.use('/api/v1', SwaggerAPIRouter);

app.use((req: express.Request, resp: express.Response) => {
  resp.status(404).send({
    msg: 'Not Found!'
  });
});

module.exports = app;

import { Router } from 'express';
import AuthController from './controllers/auth.ctrl';
import UserController from './controllers/user.ctrl';

const AuthRouter = Router();
AuthRouter.post('/login', AuthController.login);
AuthRouter.post('/register', AuthController.register);
AuthRouter.get('/activate/:activationToken', AuthController.activate);
export { AuthRouter };

const UserRouter = Router();
UserRouter.get('/', UserController.getAll);
export { UserRouter };

const SwaggerAPIRouter = Router();
export { SwaggerAPIRouter };

import { Request, Response, NextFunction } from 'express';
import * as jwt from 'jsonwebtoken';
import * as util from 'util';
import * as crypto from 'crypto';
import * as nodemailer from 'nodemailer';
import { User } from '../models/user';
import { default as UserService } from '../services/user.srvc';

class AuthController {

  async login(req: Request, resp: Response) {
    req.assert('email', 'Email is not valid').isEmail();
    req.assert('password', 'Password cannot be blank').notEmpty();
    req.sanitize('email').normalizeEmail({gmail_remove_dots: false});

    const errors = req.validationErrors();

    if (errors) {
      return resp.status(401).send({
        msg: errors,
        code: 406
      });
    }
    try {
      const user: User = await UserService.findByEmail(req.body.email);
      if (!user) {
        return resp.status(404).send({
          msg: 'User not found',
          code: 404
        });
      }
      const isSamePass = await UserService.comparePassword(req.body.password, user.password);
      if (isSamePass) {
        const token = jwt.sign({
          email: user.email,
          role: user.role,
          username: user.username
        }, process.env.JWT_SECRET, {expiresIn: '1h'});
        return resp.status(200).send({token: token});
      } else {
        return resp.status(401).send({
          msg: 'Unauthorized',
          status: 401
        });
      }
    } catch (error) {
      return resp.status(400).send({
        msg: error,
        code: 400
      });
    }
  }

  async register(req: Request, res: Response, next: NextFunction) {
    req.assert('password', 'Password cannot be blank').notEmpty();
    req.assert('fname', 'First name must be specified').notEmpty();
    req.assert('lname', 'Last name must be specified').notEmpty();
    req.assert('username', 'Username must be specified').notEmpty();
    req.assert('role', 'Role must be specified').notEmpty();

    req.assert('email', 'Email is not valid').isEmail();
    req.sanitize('email').normalizeEmail({gmail_remove_dots: false});

    const errors = req.validationErrors();

    if (errors) {
      return res.status(401).send({
        msg: errors,
        status: 401
      });
    }
    const user: User = req.body;
    try {
      // Check if user already exists
      const existingUser = await UserService.findByUsernameOrEmail(user.username, user.email);
      if (existingUser) {
        return res.status(409).send({
          msg: 'User already exists',
          status: 409
        });
      }
      // Generate activation token
      const qRandomBytes = (util as any).promisify(crypto.randomBytes);
      const cryptedValue = await qRandomBytes(16);
      user.activationToken = cryptedValue.toString('hex');
      user.activationExpires = new Date(Date.now() + 3600000); // 1 hour
      // Send activation email
      const transporter = nodemailer.createTransport({
        host: process.env.SMTP_HOST,
        port: Number(process.env.SMTP_PORT),
        secure: process.env.NODE_ENV === 'production',
        socketTimeout: 5000,
        logger: true,
        auth: {
          user: process.env.SMTP_USER,
          pass: process.env.SMTP_PASSWORD
        }
      });

      const mailOptions = {
        to: user.email,
        from: process.env.SMTP_USER,
        subject: 'Account activation',
        text: `You are receiving this email because you (or someone else) have requested account activation.\n\n
          Please click on the following link, or paste this into your browser to complete the process:\n\n
          http://${req.headers.host}/auth/activate/${user.activationToken}\n\n
          If you did not request this, please ignore this email\n`
      };
      await transporter.sendMail(mailOptions);
      const savedUser: User = await UserService.save(user);
      res.status(200).send(savedUser);
    } catch (error) {
      console.log(error);
      res.status(400).send({
        msg: 'Unable to send email',
        status: 400
      });
    }
  }

  async activate(req: Request, res: Response) {
    try {
      const user: User = await UserService.findOneAndUpdate(req.params.activationToken);
      const token = jwt.sign({
        email: user.email,
        role: user.role,
        username: user.username
      }, process.env.JWT_SECRET, {expiresIn: '1h'});
      return res.status(200).send({token: token});
    } catch (error) {
      console.log(error);
      res.status(400).send({
        msg: 'Activation token expired, please register again',
        status: 400
      });
    }
  }

}

export default new AuthController();

import { Request, Response } from 'express';
import { default as UserService } from '../services/user.srvc';

class UserController {

  async getAll(req: Request, resp: Response) {
    try {
      const users = await UserService.findAll();
      resp.status(200).send(users);
    } catch (error) {
      resp.send({
        msg: 'Not found',
        status: 404
      });
    }
  }
}

export default new UserController();

import * as mongoose from 'mongoose';
import { AuthToken } from '../models/auth-token';
import { Profile } from '../models/profile';
import * as bcrypt from 'bcrypt-nodejs';
import * as util from 'util';
import { User } from '../models/user';

export type UserType = mongoose.Document & {

  email: string,
  username: string,
  password: string,
  role: string,

  active: boolean,

  passwordResetToken: string,
  passwordResetExpires: Date,

  activationToken: string,
  activationExpires: Date,

  tokens: Array<AuthToken>,

  profile: Profile,

  comparePassword: (candidatePassword: string, cb: (err: any, isMatch: any) => {}) => void
};

const UserSchema = new mongoose.Schema({
  email: {type: String, unique: true},
  username: String,
  password: String,
  role: String,

  active: Boolean,

  passwordResetToken: String,
  passwordResetExpires: Date,

  activationToken: String,
  activationExpires: Date,

  profile: {
    fname: String,
    lname: String,
    info: String
  }
}, {timestamps: true});

/**
 * Password hash middleware.
 */
UserSchema.pre('save', function save(next) {
  const user = this;
  if (!user.isModified('password')) {
    return next();
  }
  bcrypt.genSalt(10, (err, salt) => {
    if (err) {
      return next(err);
    }
    bcrypt.hash(user.password, salt, undefined, (err: mongoose.Error, hash) => {
      if (err) {
        return next(err);
      }
      user.password = hash;
      next();
    });
  });
});

UserSchema.methods.comparePassword = function (candidatePassword: string) {
  const qCompare = (util as any).promisify(bcrypt.compare);
  return qCompare(candidatePassword, this.password);
};

type UserType = User & mongoose.Document;
const UserRepository = mongoose.model<UserType>('User', UserSchema);
export default UserRepository;

import { User } from '../models/user';
import * as bcrypt from 'bcrypt-nodejs';
import * as util from 'util';
import UserRepository, { UserType } from '../schemas/user.schema';

/**
 * @class UserService
 */
class UserService {

  /**
   * @description Fetches single user from the storage by email
   * @param email
   * @returns {Promise<User>}
   */
  async findByEmail(email): Promise<User> {
    const user: UserType = await UserRepository.findOne({email: email});
    return user;
  }

  /**
   * @description Fetches single user from the storage by email or username
   * @param username
   * @param email
   * @returns {Promise<User>}
   */
  async findByUsernameOrEmail(username, email): Promise<User> {
    const user: User = await UserRepository.findOne({$or: [{email: email}, {username: username}]});
    return user;
  }

  /**
   * @description Saves the user in the storage
   * @param {User} user
   * @returns {Promise<User>}
   */
  async save(user: User): Promise<User> {
    return (await new UserRepository(user).save()).toObject({ virtuals: true });
  }

  /**
   * @description Fetches single user by activationToken and sets active flag
   * @param activationToken
   * @returns {Promise<User>}
   */
  async findOneAndUpdate(activationToken): Promise<User> {
    const user: User = await UserRepository.findOneAndUpdate({activationToken: activationToken}, {active: true}, {new: true});
    return user;
  }

  /**
   * @description Fetches all users from the storage
   * @returns {Promise<User[]>}
   */
  async findAll(): Promise<User[]> {
    return await UserRepository.find() as User[];
  }

  /**
   * @description Deletes a single user from storage
   * @returns {Promise<void>}
   */
  async deleteOne(username: string): Promise<void> {
    return await UserRepository.deleteOne({username: username});
  }

  /**
   * @description Compares encrypted and decrypted passwords
   * @param {string} candidatePassword
   * @param storedPassword
   * @returns {boolean}
   */
  comparePassword(candidatePassword: string, storedPassword): boolean {
    const qCompare = (util as any).promisify(bcrypt.compare);
    return qCompare(candidatePassword, storedPassword);
  }
}

export default new UserService();
